function setTheme(){const e=window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches;body.className=e?"dark":"light"}function toggleTheme(){body.classList.toggle("dark"),body.classList.toggle("light")}const body=document.body,themeToggle=document.getElementById("theme-toggle");setTheme(),themeToggle.addEventListener("click",toggleTheme);